import React, { Component } from "react";
//import BrowserRouter from "react-router-dom";
import axios from "axios";
import "./authlayout.css";


class Search extends Component {
    constructor(props) {
        super(props)
        this.state = {
            lastName: "",
        };

        this.handleInput = this.handleInput.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    handleInput(event) {
        const target = event.target;
        this.setState({
          [target.name]: target.value,
        });
      }
  // when click 'submit' button this funtion will get call
    handleSubmit(event) {
    event.preventDefault();
    // getting  data from out of 'state' to add to table database
    axios
      .post("https://localhost:44388/api/users/searchuser", {

        lastName: this.state.lastName,
      })
      .then((res) => {
        console.log(res);
        console.log(res.db);
        // go to listuser page
       // this.props.history.push('/listuser')
      });
  }


    render() {
        return(
        <div className="input-form">
        <React.Fragment>
          <br></br>
          <form onSubmit={this.handleSubmit}>
            <br></br>
            <div>
              <input
                type="text"
                // holde current value
                id="lastName"
                name="lasttName"
                value={this.state.lastName}
                placeholder="Last Name"
                // after got input then pass 'event' into handleChange()
                onChange={this.handleInput}
              ></input>
            </div>
            <br />
            {/* put button on the form 'finished' */}
            <input type="submit" color="primary" value="Submit"></input>
          </form>
        </React.Fragment>
        </div>
      );
    }

}

export default Search;
